const badges = {
  10: 'Gold Badge',
  8: 'Silver Badge',
  6: 'Bronze Badge'
};

let leaderboard = [];

startButton.addEventListener('click', startGame);
restartButton.addEventListener('click', resetGame);

function startGame() {
  startButton.classList.add('hide');
  resultContainer.classList.add('hide');
  leaderboardContainer.classList.add('hide');
  shuffledQuestions = questions.sort(() => Math.random() - 0.5);
  currentQuestionIndex = 0;
  correctAnswers = 0;
  points = 0;
  questionContainerElement.classList.remove('hide');
  timerContainer.classList.remove('hide');
  timeLeft = 60;
  timerElement.innerText = timeLeft;
  timer = setInterval(updateTimer, 1000);
  setNextQuestion();
}

function updateTimer() {
  timeLeft--;
  timerElement.innerText = timeLeft;
  if (timeLeft === 0) {
    clearInterval(timer);
    showResults();
  }
}

function setNextQuestion() {
  resetState();
  showQuestion(shuffledQuestions[currentQuestionIndex]);
}

function showQuestion(question) {
  questionElement.innerText = question.question;
  if (question.image) {
    questionImageElement.src = question.image;
    questionImageElement.classList.remove('hide');
  } else {
    questionImageElement.classList.add('hide');
  }
  question.answers.forEach(answer => {
    const button = document.createElement('button');
    button.innerText = answer.text;
    button.classList.add('answer-btn');
    if (answer.correct) {
      button.dataset.correct = answer.correct;
    }
    button.addEventListener('click', selectAnswer);
    answerButtonsElement.appendChild(button);
  });
}

function resetState() {
  while (answerButtonsElement.firstChild) {
    answerButtonsElement.removeChild(answerButtonsElement.firstChild);
  }
  questionImageElement.classList.add('hide');
}

function selectAnswer(e) {
  const selectedButton = e.target;
  const correct = selectedButton.dataset.correct === 'true';
  if (correct) {
    correctAnswers++;
    points += 10;
  }
  setStatusClass(selectedButton, correct);
  Array.from(answerButtonsElement.children).forEach(button => {
    setStatusClass(button, button.dataset.correct === 'true');
  });
  if (shuffledQuestions.length > currentQuestionIndex + 1) {
    currentQuestionIndex++;
    setTimeout(setNextQuestion, 1000);
  } else {
    clearInterval(timer);
    showResults();
  }
}

function setStatusClass(element, correct) {
  if (correct) {
    element.classList.add('correct');
  } else {
    element.classList.add('wrong');
  }
}

function showResults() {
  questionContainerElement.classList.add('hide');
  timerContainer.classList.add('hide');
  resultContainer.classList.remove('hide');
  resultText.innerText = `You got ${correctAnswers} out of ${questions.length} correct! Your score is ${points}.`;
  const badge = getBadge(correctAnswers);
  if (badge) {
    badgeText.innerText = `Congratulations! You earned a ${badge}.`;
  } else {
    badgeText.innerText = '';
  }
  updateLeaderboard();
  displayLeaderboard();
}

function getBadge(score) {
  for (let key in badges) {
    if (score >= key) {
      return badges[key];
    }
  }
  return null;
}

function updateLeaderboard() {
  const playerName = prompt("Enter your name for the leaderboard:");
  leaderboard.push({ name: playerName, score: points });
  leaderboard.sort((a, b) => b.score - a.score);
}

function displayLeaderboard() {
  leaderboardContainer.classList.remove('hide');
  leaderboardElement.innerHTML = leaderboard
    .map(player => `<li>${player.name}: ${player.score} points</li>`)
    .join('');
}

function resetGame() {
  startButton.innerText = 'Restart Quiz';
  startButton.classList.remove('hide');
  questionContainerElement.classList.add('hide');
  timerContainer.classList.add('hide');
  resultContainer.classList.add('hide');
  leaderboardContainer.classList.add('hide');
}
